import React from "react";
import { ITodo } from "../interfaces";
import { useAppSelector, useAppDispatch } from "../store/hooks";
import { deleteTodo, updateTodo } from "../store/slices/todo.slice";
import { List, ListItem, Divider, Typography, Checkbox } from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import EditIcon from '@mui/icons-material/Edit';

declare var confirm: (question: string) => boolean

export const TodoList: React.FC = () => {
    const todos = useAppSelector((state) => state.todos.data);
    const dispatch = useAppDispatch();
    console.log(todos);

    if (todos.length === 0) {
        return <p className="center">There is no activities to do yet! </p>
    }

    const removeHandler = (event: React.MouseEvent, id: number) => {
        event.preventDefault();
        const shouldRemove = confirm(
            'Are you sure, you want to delete this activity?'
        );

        if (shouldRemove) {
            dispatch(deleteTodo(id))
        }

    }

    const handleChange = (event: React.ChangeEvent<HTMLInputElement>, item: ITodo) => {
        const todoCopy = { ...item }
        todoCopy.completed = event.target.checked
        dispatch(updateTodo(todoCopy))
    }

    return (
        <List sx={{ width: '50%', margin: 'auto' }}>
            {todos.map(todo => {
                return (
                    <ListItem alignItems="flex-start" key={todo.id} style={{ border: '1px solid blue', margin: '0 10px' }} >
                        <Checkbox checked={todo.completed} onChange={(event) => handleChange(event, todo)} />
                        <Typography
                            sx={{ display: 'inline' }}
                            variant="body2"
                        >
                            {todo.title}
                        </Typography>
                        <EditIcon color='info' />
                        <DeleteIcon onClick={(event) => removeHandler(event, todo.id)} color='error' />
                        <Divider variant="inset" />
                    </ListItem>
                )
            })}
        </List>
    )
}
